package valiblackdragon.mtmobile.phoneMenus;import dev.dbassett.skullcreator.SkullCreator;import io.github.bananapuncher714.nbteditor.NBTEditor;import org.bukkit.Bukkit;import org.bukkit.ChatColor;import org.bukkit.Material;import org.bukkit.entity.Player;import org.bukkit.inventory.Inventory;import org.bukkit.inventory.ItemStack;import org.bukkit.inventory.meta.ItemMeta;import valiblackdragon.mtmobile.events.openMobile;import valiblackdragon.mtmobile.main;public class mainMenu {    public openMobile tel;    private main plugin;    public mainMenu(main plugin) {        this.plugin = plugin;    }    public void mainMenu(Player player){        Inventory homeScreen = Bukkit.createInventory(player, 27, ChatColor.AQUA + "Home screen");        ItemStack call = new ItemStack(Material.BLAZE_POWDER);        ItemStack callIcon = NBTEditor.set(call, true, "NoClick");        ItemMeta callIconName = callIcon.getItemMeta();        callIconName.setDisplayName(ChatColor.RED + "Bel iemand");        ItemStack callItem = NBTEditor.set(call, "callmenu", "direct");        ItemStack backgroundItem = new ItemStack(Material.STAINED_GLASS_PANE);        ItemStack backgroundItemIcon = NBTEditor.set(backgroundItem, true, "NoClick");        ItemMeta menuItemName = backgroundItemIcon.getItemMeta();        menuItemName.setDisplayName(ChatColor.AQUA + "");        backgroundItemIcon.setItemMeta(menuItemName);        String contactskull = "eyJ0ZXh0dXJlcyI6eyJTS0lOIjp7InVybCI6Imh0dHA6Ly90ZXh0dXJlcy5taW5lY3JhZnQubmV0L3RleHR1cmUvNmViYmJkYmEzNzI5NjNjOWQ2ZDMzMjhjMjliZjEyM2FlMDlkMzBjZTdiYTNhMDU3Y2VkNjA2YzFjODAyOGI3YiJ9fX0=";        ItemStack blockitem = SkullCreator.itemFromBase64(contactskull);        ItemMeta contactmeta = blockitem.getItemMeta();        contactmeta.setDisplayName("4");        blockitem.setItemMeta(contactmeta);        ItemStack contactitem = NBTEditor.set(blockitem, "callmenu", "direct");        for (int size = 0; size < 27; size++) {            ItemStack item = player.getInventory().getItem(size);            if(!(item == null)){}            homeScreen.setItem(size, backgroundItemIcon);        }        homeScreen.setItem(10, callItem);        homeScreen.setItem(11, contactitem);        player.openInventory(homeScreen);    }}